import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Reports() {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/reports')
      .then(res => setReports(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleAction = (id, action) => {
    axios.post(`http://localhost:5000/reports/${id}`, { action })
      .then(() => window.location.reload())
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>üö© Flagged Content</h2>
      {reports.map(report => (
        <div key={report.id}>
          <p>{report.text}</p>
          <p>Label: {report.label}</p>
          <p>Status: {report.status}</p>
          <button onClick={() => handleAction(report.id, 'approve')}>‚úÖ Approve</button>
          <button onClick={() => handleAction(report.id, 'reject')}>‚ùå Reject</button>
          <hr />
        </div>
      ))}
    </div>
  );
}

export default Reports;
