import React from 'react';
import './Home.css';
import { useNavigate } from 'react-router-dom';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

function Home() {
    const navigate = useNavigate();

    React.useEffect(() => {
        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('3d-clock').appendChild(renderer.domElement);

        // Clock object
        const clockGroup = new THREE.Group();

        const ringGeometry = new THREE.TorusGeometry(5, 0.2, 16, 100);
        const ringMaterial = new THREE.MeshBasicMaterial({ color: 0x0077ff });
        const ring1 = new THREE.Mesh(ringGeometry, ringMaterial);
        const ring2 = new THREE.Mesh(ringGeometry, ringMaterial);
        ring2.rotation.x = Math.PI / 2;

        clockGroup.add(ring1);
        clockGroup.add(ring2);

        const clockFaceGeometry = new THREE.CircleGeometry(4.5, 32);
        const clockFaceMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const clockFace = new THREE.Mesh(clockFaceGeometry, clockFaceMaterial);
        clockFace.rotation.x = Math.PI / 2;
        clockGroup.add(clockFace);

        scene.add(clockGroup);

        // Camera position
        camera.position.z = 10;

        // Controls
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableZoom = true;

        // Animation loop
        const animate = function () {
            requestAnimationFrame(animate);

            clockGroup.rotation.y += 0.01;

            controls.update();
            renderer.render(scene, camera);
        };

        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = window.innerWidth;
            const height = window.innerHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        });
    }, []);

    return (
        <div className="home-container">
            <h1>AI Content Moderation System</h1>
            <p>
                Our system uses AI to detect and remove inappropriate, harmful, or copyrighted content 
                from your platform in real-time.
            </p>

            <div id="3d-clock" className="three-d-clock"></div>

            <div className="card">
                <h3>Ready to Moderate?</h3>
                <p>Navigate to the moderation panel to start moderating content.</p>
                <button onClick={() => navigate('/moderate')}>Go to Moderation</button>
            </div>

            <div className="card">
                <h3>View Reports</h3>
                <p>Check out flagged content reports and take necessary actions.</p>
                <button onClick={() => navigate('/reports')}>View Reports</button>
            </div>
        </div>
    );
}

export default Home;
